&lt;script&gt;
    function imageCropper() {
        return {
            imageUrl: null,
            previewUrl: null,
            aspectRatio: &#039;4/3&#039;, // default selected
            cropper: null,
            description: null,
            success: &#039;&#039;,
            error: &#039;&#039;,

            init() {
                this.$watch(&#039;aspectRatio&#039;, () =&gt; {
                    if (this.cropper) {
                        this.cropper.setAspectRatio(this.aspectRatio === &#039;null&#039; ? NaN : eval(this.aspectRatio));
                    }
                });
            },

            handleFile(event) {
                const file = event.target.files[0];
                if (!file) return;

                const reader = new FileReader();
                reader.onload = (e) =&gt; {
                    this.imageUrl = e.target.result;
                    this.$nextTick(() =&gt; this.initCropper());
                };
                reader.readAsDataURL(file);
            },

            initCropper() {
                this.success = &#039;&#039;;
                this.error = &#039;&#039;;
                if (this.cropper) this.cropper.destroy();
                const image = this.$refs.image;
                this.cropper = new Cropper(image, {
                    aspectRatio: eval(this.aspectRatio),
                    viewMode: 1,
                    autoCropArea: 1,
                });
            },

            showPreview() {
                if (!this.cropper) return;
                const canvas = this.cropper.getCroppedCanvas();
                this.previewUrl = canvas.toDataURL();
            },

            reset() {
                this.imageUrl = null;
                this.previewUrl = null;
                if (this.cropper) {
                    this.cropper.destroy();
                    this.cropper = null;
                }
                this.$refs.fileInput.value = null;
                this.description = null
            },

            submit() {
                const canvas = this.cropper.getCroppedCanvas();
                canvas.toBlob((blob) =&gt; {
                    const formData = new FormData();
                    formData.append(&#039;image&#039;, blob, &#039;cropped.png&#039;);
                    formData.append(&#039;description&#039;, this.description)

                    fetch(&#039;/upload/proses&#039;, {
                            method: &#039;POST&#039;,
                            body: formData,
                            credentials: &#039;include&#039;, // WAJIB supaya cookie terkirim
                            headers: {
                                &#039;X-Requested-With&#039;: &#039;XMLHttpRequest&#039; // agar isAJAX() == true
                            }
                        })
                        .then(async res =&gt; {
                            if (!res.ok) {
                                // Manual throw error dengan status

                                const data = await res.json(); // &lt;- ini penting!
                                throw {
                                    status: res.status,
                                    message: data.message
                                };
                            }


                            return res.json();
                        })
                        .then(data =&gt; {
                            this.success = data.message;
                            this.reset()
                            // ⏱ Reload setelah 3 detik
                            setTimeout(() =&gt; location.reload(), 1000);
                        })
                        .catch(err =&gt; {
                            if (err.status === 401) {
                                window.location.href = &#039;/login&#039;;
                            } else {
                                this.error = err.message;
                                this.reset()
                            }
                        });
                }, &#039;image/png&#039;);
            }
        };
    }

    function removeImage(el, id) {
        if (!confirm(&#039;Yakin ingin menghapus gambar ini?&#039;)) return;

        fetch(&#039;/upload/delete/&#039; + id, {
                method: &#039;DELETE&#039;
            })
            .then(res =&gt; res.json())
            .then(data =&gt; {
                if (data.success) {
                    el.remove(); // hapus dari DOM
                } else {
                    alert(&#039;Gagal menghapus gambar.&#039;);
                }
            })
            .catch(err =&gt; {
                alert(&#039;Terjadi kesalahan.&#039;);
            });
    }
&lt;/script&gt;
